import axios from "axios";
import { addDigimon } from "./actions";
import { toast } from "react-toastify";

const addDigimonsThunk = (digimon, setError) => (dispatch) => {
  axios
    .get(`https://digimon-api.herokuapp.com/api/digimon/name/${digimon}`)
    .then((response) => dispatch(addDigimon(response.data[0].name)))
    .then((response) => console.log(response))
    .catch(() =>
      toast.warn("Digimon não encontrado, verifique o nome !", {
        position: "top-right",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      })
    );
};

export default addDigimonsThunk;
